import React from 'react';
import { connect } from 'react-redux';
import { getMovieDetail } from '../../actions/index';
import './Movie.css';

class Movie extends React.Component {

    componentDidMount(){
        this.props.getMovieDetail(this.props.match.params.id)
    }

    render() {
        return (
            <div>
                <div key={this.props.movie_detail.id} className='movie'>
                    <h1>{this.props.movie_detail.title}</h1>
                    <img src={this.props.movie_detail.poster} alt='Poster' className='img'/>
                    <h1>{this.props.movie_detail.actors}</h1>
                    <h1>{this.props.movie_detail.director}</h1>
                    <h1>{this.props.movie_detail.writer}</h1>
                    <h1>{this.props.movie_detail.genre}</h1>
                    <h1>{this.props.movie_detail.year}</h1>
                    <h1>{this.props.movie_detail.rated}</h1>
                    <h1>{this.props.movie_detail.runtime}</h1>
                    <h1>{this.props.movie_detail.rating}</h1>
                    <p>{this.props.movie_detail.plot}</p>
                </div>
            </div>
        );
    }
}

function mapStateToProps(state){
    return {
        movie_detail : state.movieDetail
    }
}


export default connect(mapStateToProps, {getMovieDetail})(Movie);
